mapper.insert:
INSERT INTO place (
    opt
  , title
  , category
  , post_code
  , main_address
  , sub_address
  , district
  , neighborhood
  , tel
  , start_time
  , end_time
  , close_day
  , parking
)
VALUES (
    #{opt}
  , #{title}
  , #{category}
  , #{postCode}
  , #{mainAddress}
  , #{subAddress}
  , #{district}
  , #{neighborhood}
  , #{tel}
  , #{startTime}
  , #{endTime}
  , #{closeDay}
  , #{parking}
)

INSERT INTO place_review(
    place_cd
  , user_cd
  , file_cd
  , contents
  , score
) VALUES (
    #{placeCd}
  , #{userCd}
  , #{fileCd}
  , #{contents}
  , #{score}
)
