mapper.insert:
INSERT INTO place (
    cd
  , opt
  , title
  , category
  , post_code
  , main_address
  , sub_address
  , district
  , neighborhood
  , tel
  , start_time
  , end_time
  , close_day
  , parking
)
VALUES (
    #{cd}
  , #{opt}
  , #{title}
  , #{category}
  , #{postCode}
  , #{mainAddress}
  , #{subAddress}
  , #{district}
  , #{neighborhood}
  , #{tel}
  , #{startTime}
  , #{endTime}
  , #{closeDay}
  , #{parking}
)

mapper.select:
SELECT T1.* , C1.cd_nm AS district_nm
, C2.cd_nm AS category_nm
, ( SELECT COUNT( user_cd ) as cnt FROM place_like WHERE place_cd = T1.cd ) AS likes , ( SELECT new_nm as new_nm FROM file WHERE place_cd = T1.cd ORDER BY cd LIMIT 1 ) AS new_nm FROM place T1 LEFT JOIN cdmgr C1 ON ( C1.cd = 'district' AND C1.key_cd = T1.district )
LEFT JOIN cdmgr C2 ON ( C2.cd = 'food' AND C2.key_cd = T1.category )
LEFT JOIN cdmgr C2 ON ( C2.cd = 'hospital' AND C2.key_cd = T1.category )
WHERE T1.opt = #{opt}
AND T1.title LIKE CONCAT_WS('', '%', #{title}, '%')
AND T1.parking = #{parking}
AND T1.category IN
#{category}
AND T1.district IN
#{district}
ORDER BY T1.cd DESC T1.views DESC likes DESC -- offset 인덱스 부터 recordSize 개수만큼만 출력 LIMIT #{offset}, #{recordSize}

        SELECT
--          recordSize의 기본값은 9로 세팅
            CEIL( COUNT( cd ) / #{recordSize} ) AS CNT
        FROM
            place
        WHERE
            opt = #{opt}

            AND title LIKE CONCAT_WS('', '%', #{title}, '%')


            AND parking = #{parking}


            AND category IN

                #{category}



            AND district IN

                #{district}

mapper.delete:
DELETE FROM
    place_like
WHERE
      place_cd = #{placeCd}
  AND user_cd  = #{userCd}

mapper.insert:
INSERT INTO place_review(
    place_cd
  , user_cd
  , file_cd
  , contents
  , score
) VALUES (
    #{placeCd}
  , #{userCd}
  , #{fileCd}
  , #{contents}
  , #{score}
)
